mapper.select:
select
t.miniprogram_template_id,t.wx_app_id,t.organization_account,t.template_id,t.create_date,
t.update_date,t.`status`,t.reason,t.auditid,i.nick_name,bi.org_name
from t_base_organize_template t left join
t_base_wx_open_platform_merchant_info i
on t.wx_app_id=i.wx_appid left
join brand_info bi on
t.organization_account=bi.org_account

	and bi.org_name=#{params.orgName}
	 and t.is_online=#{params.isOnline}
	 and t.status=#{params.status}
	 and t.status != #{params.notStatus}

select
count(*)
from t_base_organize_template t left join
t_base_wx_open_platform_merchant_info i
on t.wx_app_id=i.wx_appid left
join brand_info bi on
t.organization_account=bi.org_account

	and bi.org_name=#{orgName}
	 and t.is_online=#{isOnline}
	 and t.status=#{status}
	 and t.status != #{notStatus}
